import requests 
import zipfile 
import os 
import platform 
from errors import NotSupported
from io import BytesIO
import json 
import pkg_resources
import psutil 


class Fetcher:
    def __init__(self):
        self.session = requests.Session() 
        self.ram = 3 

    @property 
    def get_ram(self):
        return round(psutil.virtual_memory().total / 1024 ** 3)

    def get_minecraft_location(self):
        system = platform.system()
        if system == 'Windows':
            appdata = os.getenv('appdata')
            return f'{appdata}\\.minecraft'
        elif system == 'Darwin':
            return '~\\Library\\Application Support\\.minecraft'
        else:
            raise NotSupported

    def get_modpack(self):
        with self.session.get('https://api.github.com/repos/SoulSen/BoostPack/releases/latest') as response:
            json = response.json()

        download_url = json['assets'][0]['browser_download_url']
        return json, download_url

    def install_modpack(self):
        minecraft_location = self.get_minecraft_location()

        if os.path.isdir(f'{minecraft_location}\\BoostPack v2'):
            return 

        _, download_url = self.get_modpack()

        with self.session.get(download_url, stream=True) as response:
            zip = zipfile.ZipFile(BytesIO(response.content))

        minecraft_location = self.get_minecraft_location()
        zip.extractall(f'{minecraft_location}\\BoostPack v2')
        self.install_version()
        self.add_launcher_profile()

    def update_modpack(self):
        minecraft_location = self.get_minecraft_location()

        if os.path.isdir(f'{minecraft_location}\\BoostPack v2'):
            with open(f'{minecraft_location}\\BoostPack v2\\version.txt') as version: 
                version = version.readline(1)
        else:
            return  

        json, _ = self.get_modpack()
        
        if pkg_resources.parse_version(json['tag_name']) > version:
            os.rmdir(f'{minecraft_location}\\BoostPack v2')
            self.install_modpack()

    def add_launcher_profile(self):
        minecraft_location = self.get_minecraft_location()

        with open(f'{minecraft_location}\\launcher_profiles.json', 'r+') as profiles:
            data = json.load(profiles)

            payload = {
                'gameDir': f'{minecraft_location}\\BoostPack v2',
                'icon': 'data:image/x-icon;base64,',
                'javaArgs': f'-XX:+UseG1GC -Xmx{self.ram}G -Xms{self.ram}G -Dsun.rmi.dgc.server.gcInterval=2147483646 -XX:+UnlockExperimentalVMOptions -XX:G1NewSizePercent=20 -XX:G1ReservePercent=20 -XX:MaxGCPauseMillis=25 -XX:G1HeapRegionSize=32M',
                'javaDir': f'{minecraft_location}\\BoostPack v2\\jdk\\bin\\javaw.exe',
                'lastVersionId': 'BoostPack',
                'name': 'BoostPack v2',
                'type': 'custom'
            }
            data['profiles']['BoostPack v2'] = payload
            profiles.seek(0)
            json.dump(data, profiles)
            profiles.truncate()

    def install_version(self):
        minecraft_location = self.get_minecraft_location()

        if not os.path.isdir(f'{minecraft_location}\\versions\\BoostPack'):
            os.mkdir(f'{minecraft_location}\\versions\\BoostPack')

        with open(f'{minecraft_location}\\versions\\BoostPack\\BoostPack.json', 'w+') as version_file:
            with open(f'.\\assets\\BoostPack.json') as data:
                data = json.load(data)

            version_file.seek(0)
            json.dump(data, version_file)
            version_file.truncate()
    
    def uninstall_modpack(self):
        minecraft_location = self.get_minecraft_location()

        if os.path.isdir(f'{minecraft_location}\\BoostPack v2'):
            os.rmdir(f'{minecraft_location}\\BoostPack v2')

            with open(f'{minecraft_location}\\launcher_profiles.json', 'r+') as profiles:
                data = json.load(profiles)
                del data['BoostPack v2']
                profiles.seek(0)
                json.dump(data, profiles)
                profiles.truncate()        
